mapper.select:


 		select * from board

		where board_state = 1 
  		and board_no = #{board_no}

mapper.update:

		update board set 
		board_orderno= board_orderno+1

		 where 
 		board_group = #{board_group} 
 		and board_orderno > #{board_orderno}
 		and board_state = 1



		update board set 
		board_title = #{board_title },
		board_password = #{board_password },
		board_content = #{board_content }

		where 
 		board_no = #{board_no}
